import React from 'react';
import { ChakraProvider, Box, Container } from '@chakra-ui/react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Navbar from './components/Navbar';
import theme from './theme';

// Placeholder components (will be replaced with actual implementations)
const WebScanner = () => <div>Web Scanner</div>;
const APIScanner = () => <div>API Scanner</div>;
const MobileScanner = () => <div>Mobile Scanner</div>;
const SourceCodeScanner = () => <div>Source Code Scanner</div>;
const BlockchainScanner = () => <div>Blockchain Scanner</div>;

function App() {
  return (
    <ChakraProvider theme={theme}>
      <Router>
        <Box minH="100vh">
          <Navbar />
          <Container maxW="container.xl" py={8}>
            <Routes>
              <Route path="/" element={<WebScanner />} />
              <Route path="/api" element={<APIScanner />} />
              <Route path="/mobile" element={<MobileScanner />} />
              <Route path="/source" element={<SourceCodeScanner />} />
              <Route path="/blockchain" element={<BlockchainScanner />} />
            </Routes>
          </Container>
        </Box>
      </Router>
    </ChakraProvider>
  );
}

export default App;
